/* eslint-disable jsx-a11y/control-has-associated-label */
/* eslint-disable react/button-has-type */

import React from 'react';
import PropTypes from 'prop-types';

class BlocklyToolboxButton extends React.PureComponent {
  constructor(...args) {
    var _temp;

    return _temp = super(...args), this.render = () => React.createElement('button', {
      text: this.props.text,
      callbackkey: this.props.callbackKey
    }), _temp;
  }

}

BlocklyToolboxButton.propTypes = {
  text: PropTypes.string.isRequired,
  callbackKey: PropTypes.string.isRequired
};

BlocklyToolboxButton.renderButton = (button, key) => {
  return React.createElement(BlocklyToolboxButton, {
    key: key,
    text: button.get('text'),
    callbackKey: button.get('callbackKey')
  });
};

export default BlocklyToolboxButton;